function App() {
   // 2. {} 중괄호를 이용 시 자바스크립트 표현식 사용 가능
   const name = '리액트';

   return (
      // 1. 반드시 부모요소로 감싸야 한다. (태그나 fragment 사용)
      <div>
         <h1>리액트</h1>
         <h2>리액트 안녕</h2>

         <h1>{name} 안녕!</h1>

         {name === '리액트' ? <h1>리액트입니다.</h1> : <h2>아닙니다.</h2>}
      </div>
   );
}

export default App;
