import React, { useReducer, useState } from 'react';

function ReducerCounter3() {
   const [count, countDispatch] = useReducer(countReducer, 0);
   const [number, setNumber] = useState(1);
   // reducer 함수: state를 바꾸는 역할
   // reducer 함수(state 값, 행위)
   function countReducer(oldCount, action) {
      if (action === 'UP') {
         return oldCount + 1;
      } else if (action === 'DOWN') {
         return oldCount - 1;
      } else if (action === 'RESET') {
         return 0;
      }
   }
   const down = () => countDispatch('DOWN');
   const reset = () => countDispatch('RESET');
   const up = () => countDispatch('UP');

   const changeNumber = () => setNumber(Number(e.target.value));
   return (
      <>
         <input type="button" value="-" onClick={down} />
         <input type="button" value="0" onClick={reset} />
         <input type="button" value="+" onClick={up} />
         <br />

         <input type="text" value={number} onChange={changeNumber} />
         <br />
         <span>{count}</span>
      </>
   );
}

export default ReducerCounter3;
